    display: flex;
    flex-direction: column;
  }

  .review-title {
    font-weight: bold;
    margin-bottom: 0.4rem;
    font-size: 1.1rem;
  }

  .review-text {
    font-size: 1rem;
    line-height: 1.5;
    color: #333;
  }

